CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;

CREATE TABLE "Abouts" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Title" text,
    "Description" text,
    "SubDescription" text,
    "Details" text,
    "ImageUrl" text,
    "CvUrl" text,
    "Phone" text,
    "Email" text,
    "Address" text,
    "FullName" text,
    "Age" text,
    "ProjectCount" text,
    "ExperienceYear" text,
    "CustomerCount" text,
    "CreatedDate" timestamp without time zone NOT NULL,
    "UpdatedDate" timestamp without time zone,
    CONSTRAINT "PK_Abouts" PRIMARY KEY ("Id")
);

CREATE TABLE "AspNetRoles" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" character varying(256),
    "NormalizedName" character varying(256),
    "ConcurrencyStamp" text,
    CONSTRAINT "PK_AspNetRoles" PRIMARY KEY ("Id")
);

CREATE TABLE "AspNetUsers" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" text,
    "Surname" text,
    "UserName" character varying(256),
    "NormalizedUserName" character varying(256),
    "Email" character varying(256),
    "NormalizedEmail" character varying(256),
    "EmailConfirmed" boolean NOT NULL,
    "PasswordHash" text,
    "SecurityStamp" text,
    "ConcurrencyStamp" text,
    "PhoneNumber" text,
    "PhoneNumberConfirmed" boolean NOT NULL,
    "TwoFactorEnabled" boolean NOT NULL,
    "LockoutEnd" timestamp with time zone,
    "LockoutEnabled" boolean NOT NULL,
    "AccessFailedCount" integer NOT NULL,
    CONSTRAINT "PK_AspNetUsers" PRIMARY KEY ("Id")
);

CREATE TABLE "ContactInfos" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Address" character varying(250),
    "Phone" character varying(50),
    "Email" character varying(100),
    "MapUrl" text,
    "CreatedDate" timestamp without time zone NOT NULL,
    "UpdatedDate" timestamp without time zone,
    CONSTRAINT "PK_ContactInfos" PRIMARY KEY ("Id")
);

CREATE TABLE "Features" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Header" text,
    "Name" text,
    "Title" text,
    "ImageUrl" text,
    CONSTRAINT "PK_Features" PRIMARY KEY ("Id")
);

CREATE TABLE "Messages" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NOT NULL,
    "Email" text NOT NULL,
    "Subject" text NOT NULL,
    "Content" text NOT NULL,
    "Date" timestamp without time zone NOT NULL,
    "IsRead" boolean NOT NULL,
    "CreatedDate" timestamp without time zone NOT NULL,
    "UpdatedDate" timestamp without time zone,
    CONSTRAINT "PK_Messages" PRIMARY KEY ("Id")
);

CREATE TABLE "NavbarItems" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Title" character varying(50) NOT NULL,
    "SectionId" character varying(50) NOT NULL,
    "IconClass" character varying(50),
    "DisplayOrder" integer NOT NULL,
    "IsActive" boolean NOT NULL,
    "CreatedDate" timestamp without time zone NOT NULL,
    "UpdatedDate" timestamp without time zone,
    CONSTRAINT "PK_NavbarItems" PRIMARY KEY ("Id")
);

CREATE TABLE "Projects" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Title" character varying(150) NOT NULL,
    "Description" text NOT NULL,
    "ImageUrl" character varying(250),
    "GithubLink" character varying(250),
    "ProjectUrl" character varying(250),
    "DisplayOrder" integer NOT NULL,
    "Platform" character varying(100),
    "CreatedDate" timestamp without time zone NOT NULL,
    "UpdatedDate" timestamp without time zone,
    CONSTRAINT "PK_Projects" PRIMARY KEY ("Id")
);

CREATE TABLE "Services" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Title" character varying(30) NOT NULL,
    "Description" character varying(100) NOT NULL,
    "IconUrl" text,
    "CreatedDate" timestamp without time zone NOT NULL,
    "UpdatedDate" timestamp without time zone,
    CONSTRAINT "PK_Services" PRIMARY KEY ("Id")
);

CREATE TABLE "SiteSettings" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "SiteTitle" character varying(100) NOT NULL,
    "MetaDescription" character varying(500),
    "FooterText" character varying(200),
    "LogoUrl" character varying(500),
    "FaviconUrl" character varying(500),
    "Email" character varying(100),
    "Phone" character varying(30),
    "FormspreeId" character varying(100),
    "GithubUrl" character varying(500),
    "LinkedinUrl" character varying(500),
    "XUrl" character varying(500),
    "InstagramUrl" character varying(500),
    "ActiveTemplate" character varying(50) NOT NULL,
    "PrimaryColor" character varying(7) NOT NULL,
    "SecondaryColor" character varying(7) NOT NULL,
    "FooterTitle" character varying(100),
    "FooterDescription" character varying(500),
    "CopyrightText" character varying(200),
    "DeveloperName" character varying(100),
    "DeveloperUrl" character varying(500),
    "CreatedDate" timestamp without time zone NOT NULL,
    "UpdatedDate" timestamp without time zone,
    CONSTRAINT "PK_SiteSettings" PRIMARY KEY ("Id")
);

CREATE TABLE "Skills" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Title" character varying(50) NOT NULL,
    "Percentage" smallint NOT NULL,
    "Category" character varying(50) NOT NULL,
    "CreatedDate" timestamp without time zone NOT NULL,
    "UpdatedDate" timestamp without time zone,
    CONSTRAINT "PK_Skills" PRIMARY KEY ("Id")
);

CREATE TABLE "Testimonials" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "ClientName" text,
    "Company" text,
    "Comment" text,
    "ImageUrl" text,
    "Title" text,
    "CreatedDate" timestamp without time zone NOT NULL,
    "UpdatedDate" timestamp without time zone,
    CONSTRAINT "PK_Testimonials" PRIMARY KEY ("Id")
);

CREATE TABLE "TickerItems" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "Text" character varying(150) NOT NULL,
    "IconClass" character varying(50),
    "DisplayOrder" integer NOT NULL,
    "IsActive" boolean NOT NULL,
    "CreatedDate" timestamp without time zone NOT NULL,
    "UpdatedDate" timestamp without time zone,
    CONSTRAINT "PK_TickerItems" PRIMARY KEY ("Id")
);

CREATE TABLE "AspNetRoleClaims" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "RoleId" integer NOT NULL,
    "ClaimType" text,
    "ClaimValue" text,
    CONSTRAINT "PK_AspNetRoleClaims" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_AspNetRoleClaims_AspNetRoles_RoleId" FOREIGN KEY ("RoleId") REFERENCES "AspNetRoles" ("Id") ON DELETE CASCADE
);

CREATE TABLE "AspNetUserClaims" (
    "Id" integer GENERATED BY DEFAULT AS IDENTITY,
    "UserId" integer NOT NULL,
    "ClaimType" text,
    "ClaimValue" text,
    CONSTRAINT "PK_AspNetUserClaims" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_AspNetUserClaims_AspNetUsers_UserId" FOREIGN KEY ("UserId") REFERENCES "AspNetUsers" ("Id") ON DELETE CASCADE
);

CREATE TABLE "AspNetUserLogins" (
    "LoginProvider" text NOT NULL,
    "ProviderKey" text NOT NULL,
    "ProviderDisplayName" text,
    "UserId" integer NOT NULL,
    CONSTRAINT "PK_AspNetUserLogins" PRIMARY KEY ("LoginProvider", "ProviderKey"),
    CONSTRAINT "FK_AspNetUserLogins_AspNetUsers_UserId" FOREIGN KEY ("UserId") REFERENCES "AspNetUsers" ("Id") ON DELETE CASCADE
);

CREATE TABLE "AspNetUserRoles" (
    "UserId" integer NOT NULL,
    "RoleId" integer NOT NULL,
    CONSTRAINT "PK_AspNetUserRoles" PRIMARY KEY ("UserId", "RoleId"),
    CONSTRAINT "FK_AspNetUserRoles_AspNetRoles_RoleId" FOREIGN KEY ("RoleId") REFERENCES "AspNetRoles" ("Id") ON DELETE CASCADE,
    CONSTRAINT "FK_AspNetUserRoles_AspNetUsers_UserId" FOREIGN KEY ("UserId") REFERENCES "AspNetUsers" ("Id") ON DELETE CASCADE
);

CREATE TABLE "AspNetUserTokens" (
    "UserId" integer NOT NULL,
    "LoginProvider" text NOT NULL,
    "Name" text NOT NULL,
    "Value" text,
    CONSTRAINT "PK_AspNetUserTokens" PRIMARY KEY ("UserId", "LoginProvider", "Name"),
    CONSTRAINT "FK_AspNetUserTokens_AspNetUsers_UserId" FOREIGN KEY ("UserId") REFERENCES "AspNetUsers" ("Id") ON DELETE CASCADE
);

CREATE INDEX "IX_AspNetRoleClaims_RoleId" ON "AspNetRoleClaims" ("RoleId");

CREATE UNIQUE INDEX "RoleNameIndex" ON "AspNetRoles" ("NormalizedName");

CREATE INDEX "IX_AspNetUserClaims_UserId" ON "AspNetUserClaims" ("UserId");

CREATE INDEX "IX_AspNetUserLogins_UserId" ON "AspNetUserLogins" ("UserId");

CREATE INDEX "IX_AspNetUserRoles_RoleId" ON "AspNetUserRoles" ("RoleId");

CREATE INDEX "EmailIndex" ON "AspNetUsers" ("NormalizedEmail");

CREATE UNIQUE INDEX "UserNameIndex" ON "AspNetUsers" ("NormalizedUserName");

